import React, { Component} from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom';

export default class AddLab_c extends Component {

    constructor(props){
        super(props);
        this.state={
            name:"",
            id:"",
            age:"",
            gender:"",
            date:"",
            labTest:"",
            labNo:"",
            technologist:"",
            status:"",
            nameError:"",
            idError:"",
            dateError:"",
            labTestError:""

        }
    }

    // handle input changes
    handleInputChange = (e) =>{
        const {name,value} = e.target;

        this.setState({
            ...this.state,
            [name]:value
        })
    }


    // validate input fields
    validate = () =>{

        let nameError="";
        let idError="";
        let dateError="";
        let labTestError="";
  
        if(!this.state.name){
          nameError = 'Please Fill the name field'
        }

        if(!this.state.id){
            idError = 'Please Fill the id field '
          }

        if(!this.state.date){
            dateError = 'Please Fill the date field '
          }

        if(!this.state.labTest){
            labTestError = 'Please Fill the test field '
          }

        if(nameError||idError||dateError||labTestError){
            this.setState({nameError,idError,dateError,labTestError});
            return false;
          }
          

        return true;

        };


    // add form data into database 
    onSubmit = (e) =>{
        e.preventDefault();

        const isValid = this.validate();

        const{name,id,age,gender,date,labTest,labNo,technologist,status} = this.state;

        const data = {
            name:name,
            id:id,
            age:age,
            gender:gender,
            date:date,
            labTest:labTest,
            labNo:labNo,
            technologist:technologist,
            status:status
            

        }

        if(isValid){
            console.log(data)

